mapper.select:
SELECT board_userno , boardno , board_title , board_content , board_ani_category , board_reg_date , board_viewcount , commentcount , likecount , fileAttached
FROM board
WHERE boardno = #{boardno}

mapper.update:
UPDATE FROM board
SET board_title = #{board_title} , board_content = #{board_content} , board_ani_category = #{board_ani_category} , fileAttached = #{fileAttached}
WHERE boardno = #{boardno} and board_userno = #{LoginId}

UPDATE board
SET fileAttached = #{fileAttached}
WHERE boardno = #{boardno}
