mapper.select:
SELECT protectboard_userno , protectboardno , protectboard_content , protectboard_imagepath , breeds , protectboard_ani_category , protectboard_reg_date  , protectstatus , deadline
FROM protectboard
WHERE protectboardno = #{protectboardno}

mapper.update:
UPDATE protectboard
SET protectboard_content = #{protectboard_content} , protectboard_imagepath = #{protectboard_imagepath} , breeds = #{breeds} , protectboard_ani_category = #{protectboard_ani_category} , protectstatus = #{protectstatus}
WHERE protectboard_userno = #{protectboard_userno} and protectboardno = #{protectboardno}
