mapper.select:
SELECT protectboard_id , protectboardno , content , protectimagepath , breeds , ani_category , writedate  , status , deadline
FROM protectboard
WHERE protectboardno = #{protectboardno}

mapper.update:
UPDATE protectboard
SET content = #{content} , protectimagepath = #{protectimagepath} , breeds = #{breeds} , ani_category = #{ani_category}
WHERE protectboard_id = #{protectboard_id} and protectboardno = #{protectboardno}
